import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

import { images } from '../../constants';

import './About.scss';

const abouts = [
  {title: 'Web Development', description: 'I am a good web developer.', imgUrl: images.about01},
  {title: 'Front End', description: 'I am a good web developer.', imgUrl: images.about02},
  {title: 'Back End', description: 'I am a good web developer.', imgUrl: images.about03},
  {title: 'Web Animations', description: 'I am a good web developer.', imgUrl: images.about04},
];

const About = () => {
  return (
    <> {/*Empty react fragment */}
      <h2 className="head-text"> I know that <span>Good Design</span> <br/> means <span>Good Business</span>
      </h2>

      <div className="app__profiles">
        {/* Get an individual "about" and the index, then instantly return a motion.div */}
        {abouts.map((about, index) => (
          <motion.div
            whileInView={{ opacity: 1 }}
            whileHover={{ scale: 1.1 }}
            transition={{ duration: 0.5, type: 'tween'}}
            className="app__profile-item"
            key={about.title + index}
          >
            <img src={about.imgUrl} alt={about.title} />
            <h2 className="bold-text" style={{ marginTop: 20 }}> {about.title} </h2>
            <p className="p-text" style={{ marginTop: 10 }}> {about.description} </p>
          </motion.div>
        ))}
      </div>
    </>
  )
}

export default About
